// --- ÄÄNIEN PAKKAUS (Base64) ---
// Muunna .mp3-tiedostosi Base64-muotoon (esim. base64.guru) ja liitä koodit tähän:
const audioUpBase64 = "data:audio/mp3;base64,data:audio/mpeg;base64,";
const audioDownBase64 = "data:audio/mp3;base64,data:audio/mpeg;base64,";

const audioUp = new Audio(audioUpBase64);
const audioDown = new Audio(audioDownBase64);

let ws, currentCrypto = 'btc', currentTimeframe = '24h', alarms = {}, timeframeData = {'1h':'0.00','24h':'0.00'}, isMuted = false, hourlyBasePrice = 0, lastAudioTime = 0, currentLiveVol = "--", hourlyVolume = "--", alertStates = {}; 

let config = { 
    btc: { name: 'Bitcoin', symbol: 'BTC', pair: 'btcusdt', source: 'binance' },
    eth: { name: 'Ethereum', symbol: 'ETH', pair: 'ethusdt', source: 'binance' },
    reef: { name: 'Reef', symbol: 'REEF', pair: 'reef_usdt', source: 'gate' }
};

// --- ALUSTUS ---
window.onload = () => {
    const saved = JSON.parse(localStorage.getItem('crypto_glass_v55'));
    if (saved) { 
        config = saved.config || config; 
        currentCrypto = saved.current || 'btc'; 
        alarms = saved.alarms || {}; 
        isMuted = saved.isMuted || false; 
    }
    updateSelectMenu(); 
    changeCrypto(); 
    updateMuteUI();
};

// --- DATA & WEBSOCKET ---
function connect() {
    if (ws) ws.close();
    const coin = config[currentCrypto];
    const isGate = (coin.source === 'gate' || coin.symbol === "REEF");
    const url = isGate ? "wss://api.gateio.ws/ws/v4/" : `wss://stream.binance.com:9443/ws/${coin.pair}@ticker`;
    
    ws = new WebSocket(url);
    if (isGate) {
        ws.onopen = () => ws.send(JSON.stringify({channel:"spot.tickers", event:"subscribe", payload:[coin.symbol+"_USDT"]}));
        ws.onmessage = (e) => { 
            const r = JSON.parse(e.data); 
            if (r.event === "update") handleTickerData(r.result.last, r.result.change_percentage, r.result.high_24h, r.result.low_24h, r.result.quote_volume); 
        };
    } else {
        ws.onmessage = (e) => { 
            const d = JSON.parse(e.data); 
            handleTickerData(d.c, d.P, d.h, d.l, d.q); 
        };
    }
}

function handleTickerData(price, change, high, low, vol) {
    const p = parseFloat(price); 
    const decimals = (config[currentCrypto].symbol === "REEF") ? 8 : (p < 10 ? 4 : 2);
    
    document.getElementById('price').innerText = '$' + p.toLocaleString(undefined, {minimumFractionDigits: decimals, maximumFractionDigits: decimals});
    
    timeframeData['24h'] = change;
    if (hourlyBasePrice > 0) timeframeData['1h'] = (((p - hourlyBasePrice) / hourlyBasePrice) * 100).toFixed(2);
    
    updateChangeDisplay();
    document.getElementById('valHigh').innerText = '$' + parseFloat(high).toLocaleString(undefined, {maximumFractionDigits: decimals});
    document.getElementById('valLow').innerText = '$' + parseFloat(low).toLocaleString(undefined, {maximumFractionDigits: decimals});
    
    currentLiveVol = parseFloat(vol).toLocaleString(undefined, {maximumFractionDigits: 0}) + ' USDT';
    updateVolDisplay(); 
    checkAlarms(p);
}

// --- HÄLYTYSLOGIIKKA (PÄIVITETTY ÄÄNILLÄ) ---
function checkAlarms(p) {
    const a = alarms[currentCrypto]; 
    if (!a) return;
    
    const now = Date.now(); 
    const cKey = currentCrypto;
    if (!alertStates[cKey]) alertStates[cKey] = { h: false, l: false };
    
    let hit = false; 
    let direction = '';
    const decimals = (config[cKey].symbol === "REEF") ? 8 : (p < 10 ? 4 : 2);
    const fmtPrice = '$' + p.toLocaleString(undefined, {minimumFractionDigits: decimals, maximumFractionDigits: decimals});

    // High Alert - Ylittää rajan
    if (a.high && p >= a.high) { 
        if (!alertStates[cKey].h) { hit = true; alertStates[cKey].h = true; direction = 'up'; } 
    } else { alertStates[cKey].h = false; }

    // Low Alert - Alittaa rajan
    if (a.low && p <= a.low) { 
        if (!alertStates[cKey].l) { hit = true; alertStates[cKey].l = true; direction = 'down'; } 
    } else { alertStates[cKey].l = false; }

    if (hit) {
        // Soitetaan suuntaan sopiva ääni koodin sisältä (max 10s välein)
        if (!isMuted && (now - lastAudioTime > 10000)) { 
            if (direction === 'up') {
                audioUp.play().catch(e => console.log("Audio up failed"));
            } else {
                audioDown.play().catch(e => console.log("Audio down failed"));
            }
            lastAudioTime = now; 
        }
        
        const target = direction === 'up' ? a.high : a.low;
        showFloatingAlert(config[cKey].symbol, direction, fmtPrice, target, false);
        
        // Aktivoi kello ja heilunta
        const wb = document.getElementById('widgetBell');
        wb.innerText = 'notifications_active';
        wb.classList.add('swinging');
    }
}

function showFloatingAlert(symbol, dir, price, target, isConfirmation = false) {
    const fa = document.getElementById('floatingAlert');
    const faIcon = document.getElementById('faIcon');
    const faTitle = document.getElementById('faTitle');
    const faSubtitle = document.getElementById('faSubtitle');
    
    fa.classList.remove('show');

    setTimeout(() => {
        if (isConfirmation) {
            fa.style.background = 'rgba(255, 255, 255, 0.12)';
            faIcon.innerText = 'task_alt';
            faIcon.style.color = '#4ade80';
            faTitle.innerText = 'SETTING SAVED';
            faSubtitle.innerText = `${symbol} alerts updated.`;
        } else {
            const isUp = dir === 'up';
            fa.style.background = isUp ? 'rgba(34, 197, 94, 0.2)' : 'rgba(239, 68, 68, 0.2)';
            faIcon.innerText = isUp ? 'trending_up' : 'trending_down';
            faIcon.style.color = isUp ? '#4ade80' : '#ff5252';
            faTitle.innerText = `${symbol} TARGET HIT!`;
            faSubtitle.innerText = `Reached $${target}`;
        }
        fa.classList.add('show');
    }, 100);
}

function dismissAlert() {
    const fa = document.getElementById('floatingAlert');
    fa.classList.remove('show');
    const wb = document.getElementById('widgetBell');
    wb.classList.remove('swinging');
    wb.innerText = 'notifications';
}

function setAlarm() { 
    const sym = config[currentCrypto].symbol;
    alarms[currentCrypto] = { 
        high: document.getElementById('alarmHigh').value, 
        low: document.getElementById('alarmLow').value 
    }; 
    alertStates[currentCrypto] = { h: false, l: false }; 
    save(); 
    showFloatingAlert(sym, 'up', null, null, true);
}

function clearAlarm() { 
    document.getElementById('alarmHigh').value = ''; 
    document.getElementById('alarmLow').value = ''; 
    delete alarms[currentCrypto]; 
    const wb = document.getElementById('widgetBell');
    wb.classList.remove('swinging');
    wb.innerText = 'notifications';
    save(); 
}

// --- UI TOIMINNOT ---
function handleSelectChange() { 
    currentCrypto = document.getElementById('cryptoSelect').value; 
    changeCrypto(); 
    save(); 
}

function changeCrypto() {
    const coin = config[currentCrypto];
    document.getElementById('cryptoIconImg').src = `https://raw.githubusercontent.com/teukka79/crypto-widget/main/${coin.symbol.toLowerCase()}.png`;
    document.getElementById('cryptoName').innerText = coin.symbol;
    document.getElementById('cryptoPair').innerText = coin.symbol + '/USDT';
    document.getElementById('alarmHigh').value = (alarms[currentCrypto] || {}).high || '';
    document.getElementById('alarmLow').value = (alarms[currentCrypto] || {}).low || '';
    connect(); 
    fetch1hData(currentCrypto);
}

function toggleMute() { 
    isMuted = !isMuted; 
    updateMuteUI(); 
    save(); 
}

function updateMuteUI() {
    const mb = document.getElementById('muteBtn'); 
    const mi = document.getElementById('muteIcon');
    mb.style.color = isMuted ? 'var(--static-red-text)' : 'var(--static-green-text)';
    mi.innerText = isMuted ? 'volume_off' : 'volume_up';
}

function setTimeframe(tf) { 
    currentTimeframe = tf; 
    document.getElementById('tf1h').classList.toggle('active', tf==='1h'); 
    document.getElementById('tf24h').classList.toggle('active', tf==='24h'); 
    updateChangeDisplay(); 
    updateVolDisplay(); 
}

function updateChangeDisplay() {
    const val = parseFloat(timeframeData[currentTimeframe]); 
    const cont = document.getElementById('priceChangeContainer');
    cont.style.color = val >= 0 ? 'var(--static-green-text)' : 'var(--static-red-text)';
    cont.style.backgroundColor = val >= 0 ? 'rgba(34,197,94,0.15)' : 'rgba(239,68,68,0.15)';
    document.getElementById('priceChange').innerText = (val >= 0 ? '+' : '') + val.toFixed(2) + '%';
}

function updateVolDisplay() {
    document.getElementById('volLabel').innerText = currentTimeframe === '1h' ? "1H VOLUME" : "24H VOLUME";
    document.getElementById('valVol').innerText = currentTimeframe === '1h' ? hourlyVolume : currentLiveVol;
}

// --- MODAALIT ---
function openAddModal() {
    document.getElementById('modalOverlay').style.display = 'flex';
    document.getElementById('modalContainer').innerHTML = `
        <h3 style="margin-top:0; font-weight:900;">ADD TOKEN</h3>
        <input type="text" id="modalInput" style="width:100%; height:54px; background:rgba(255,255,255,0.05); color:#fff; text-align:center; border:1px solid var(--glass-border); border-radius:14px; margin-bottom:20px; font-size:18px; font-weight:800; text-transform:uppercase;" placeholder="SYMBOL">
        <div style="display:flex; gap:10px;"><button class="btn-static static-green" onclick="confirmAdd()">ADD</button><button class="btn-static static-red" onclick="closeModal()">BACK</button></div>`;
    setTimeout(() => document.getElementById('modalInput').focus(), 100);
}

function confirmAdd() {
    const sym = document.getElementById('modalInput').value.toUpperCase().trim(); 
    if(!sym) return;
    const src = (sym === "REEF") ? 'gate' : 'binance';
    config[sym.toLowerCase()] = { name: sym, symbol: sym, pair: (src==='binance' ? sym.toLowerCase()+'usdt' : sym.toLowerCase()+'_usdt'), source: src };
    currentCrypto = sym.toLowerCase(); 
    updateSelectMenu(); 
    closeModal(); 
    changeCrypto(); 
    save();
}

function closeModal() { document.getElementById('modalOverlay').style.display = 'none'; }

function confirmRemove() { 
    const name = config[currentCrypto].symbol; 
    document.getElementById('modalOverlay').style.display = 'flex'; 
    document.getElementById('modalContainer').innerHTML = `<h3>REMOVE ${name}?</h3><div style="display:flex; gap:10px; margin-top:20px;"><button class="btn-static static-red" onclick="executeRemove()">DELETE</button><button class="btn-static static-green" onclick="closeModal()">KEEP</button></div>`; 
}

function executeRemove() { 
    delete config[currentCrypto]; 
    const rem = Object.keys(config); 
    currentCrypto = rem.length > 0 ? rem[0] : 'btc'; 
    save(); 
    location.reload(); 
}

function updateSelectMenu() {
    const sel = document.getElementById('cryptoSelect'); 
    sel.innerHTML = '';
    Object.keys(config).sort().forEach(k => {
        const o = document.createElement('option'); o.value = k;
        o.text = `${config[k].symbol} (${config[k].source === 'binance' ? 'Binance' : 'Gate.io'})`;
        if(k === currentCrypto) o.selected = true; 
        sel.add(o);
    });
}

// --- APUTOIMINNOT ---
async function fetch1hData(cKey) {
    const coin = config[cKey];
    try {
        const url = (coin.source === 'binance' && coin.symbol !== "REEF") 
            ? `https://api.binance.com/api/v3/klines?symbol=${coin.pair.toUpperCase()}&interval=1h&limit=1`
            : `https://api.gateio.ws/api/v4/spot/candlesticks?currency_pair=${coin.symbol}_USDT&interval=1h&limit=1`;
        const res = await fetch(url); 
        const d = await res.json();
        hourlyBasePrice = (coin.source === 'binance') ? parseFloat(d[0][1]) : parseFloat(d[0][5]);
        hourlyVolume = (coin.source === 'binance') 
            ? (parseFloat(d[0][5]) * hourlyBasePrice).toLocaleString(undefined, {maximumFractionDigits: 0}) + ' USDT'
            : parseFloat(d[0][1]).toLocaleString(undefined, {maximumFractionDigits: 0}) + ' USDT';
        updateVolDisplay();
    } catch(e) {}
}

function save() { 
    localStorage.setItem('crypto_glass_v55', JSON.stringify({ config, current: currentCrypto, alarms, isMuted })); 
}

// Kello
setInterval(() => { 
    document.getElementById('liveClock').innerText = new Date().toLocaleTimeString('fi-FI', { hour12: false }); 
}, 1000);